import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaBell, FaRegEnvelope, FaRegEnvelopeOpen } from 'react-icons/fa';
import '../../styles/Profile.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const NotificationsTab = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadNotifs = async () => {
      try {
        // ‚úÖ OBTENER TOKEN DE SESSIONSTORAGE
        const token = sessionStorage.getItem('token');
        
        console.log('üîÑ Cargando notificaciones...');
        console.log('üîë Token encontrado:', token ? 'S√ç' : 'NO');
        
        if (!token) {
          console.log('‚ùå No hay token en sessionStorage');
          setError('No est√°s autenticado. Por favor, inicia sesi√≥n.');
          setLoading(false);
          return;
        }

        const res = await axios.get(`${API_URL}/notifications`, { 
          headers: { Authorization: `Bearer ${token}` } 
        });

        console.log('‚úÖ Notificaciones cargadas:', res.data.notifications?.length || 0);
        setNotifications(res.data.notifications || []);
      } catch (err) { 
        console.error('‚ùå Error al cargar notificaciones:', err.response?.status, err.message);
        
        if (err.response?.status === 401) {
          setError('Tu sesi√≥n ha expirado. Por favor, vuelve a iniciar sesi√≥n.');
        } else if (err.response?.status === 404) {
          // Si el endpoint no existe, no mostrar error, solo dejar vac√≠o
          console.log('‚ÑπÔ∏è Endpoint de notificaciones no disponible');
          setNotifications([]);
        } else {
          setError('Error al cargar notificaciones. Intenta recargar la p√°gina.');
        }
      } finally { 
        setLoading(false); 
      }
    };
    loadNotifs();
  }, []);

  const markAsRead = async (notificationId) => {
    try {
      const token = sessionStorage.getItem('token');
      await axios.put(
        `${API_URL}/notifications/${notificationId}/read`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Actualizar estado local
      setNotifications(prev => 
        prev.map(n => n._id === notificationId ? { ...n, read: true } : n)
      );
    } catch (err) {
      console.error('Error al marcar como le√≠da:', err);
    }
  };

  if (loading) return <div className="tab-loading">Buscando nuevos mensajes...</div>;

  if (error) return (
    <div className="empty-state">
      <FaBell size={40} />
      <h3>Error</h3>
      <p>{error}</p>
    </div>
  );

  return (
    <div className="notifications-tab">
      <div className="tab-header">
        <h2>Notificaciones</h2>
        {notifications.length > 0 && (
          <span className="notif-count">
            {notifications.filter(n => !n.read).length} sin leer
          </span>
        )}
      </div>
      
      {notifications.length === 0 ? (
        <div className="empty-state">
          <FaBell size={40} />
          <p>No tienes mensajes nuevos por ahora.</p>
        </div>
      ) : (
        <div className="notif-list">
          {notifications.map(n => (
            <div 
              key={n._id} 
              className={`notif-card ${n.read ? 'read' : 'unread'}`}
              onClick={() => !n.read && markAsRead(n._id)}
              style={{ cursor: !n.read ? 'pointer' : 'default' }}
            >
              <div className="notif-icon">
                {n.read ? <FaRegEnvelopeOpen /> : <FaRegEnvelope />}
              </div>
              <div className="notif-info">
                <h4>{n.title}</h4>
                <p>{n.message}</p>
                <small>{new Date(n.createdAt).toLocaleString('es-ES', {
                  year: 'numeric',
                  month: 'short',
                  day: 'numeric',
                  hour: '2-digit',
                  minute: '2-digit'
                })}</small>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default NotificationsTab;
