import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client'; // Importante para el tiempo real
import '../../styles/AdminChat.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const SOCKET_URL = API_URL.replace('/api', '');

let socket = null;

const AdminChat = () => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedChat, setSelectedChat] = useState(null);
  const [message, setMessage] = useState('');
  const [view, setView] = useState('open');
  const messagesEndRef = useRef(null);

  // --- L칍GICA DE TIEMPO REAL ---
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    // Inicializamos el socket si no existe
    if (!socket) {
      socket = io(SOCKET_URL, { transports: ['websocket'] });
    }

    // Autenticamos al admin en el socket
    socket.emit('authenticate', token);

    // ESCUCHAR NUEVOS MENSAJES DEL CLIENTE
    // El evento 'new_admin_message' debe ser el que emite tu backend
    socket.on('new_admin_message', (updatedChat) => {
      refreshChatList(updatedChat);
    });

    // Escuchar cuando el cliente env칤a mensaje (dependiendo de tu backend)
    socket.on('chat_updated', (updatedChat) => {
      refreshChatList(updatedChat);
    });

    loadChats();

    return () => {
      socket.off('new_admin_message');
      socket.off('chat_updated');
    };
  }, [view]);

  // Funci칩n para actualizar la lista y el chat abierto sin recargar p치gina
  const refreshChatList = (updatedChat) => {
    setChats(prevChats => {
      const exists = prevChats.find(c => c._id === updatedChat._id);
      if (exists) {
        return prevChats.map(c => c._id === updatedChat._id ? updatedChat : c);
      }
      return [updatedChat, ...prevChats];
    });

    // Si el chat actualizado es el que el admin tiene abierto, actualizar mensajes
    setSelectedChat(prevSelected => {
      if (prevSelected?._id === updatedChat._id) {
        return updatedChat;
      }
      return prevSelected;
    });
  };

  // Auto-scroll al recibir mensajes
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [selectedChat?.messages]);

  // --- L칍GICA DE DATOS ---
  const loadChats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/chat/admin/chats?status=${view}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setChats(response.data || []);
    } catch (err) {
      setError('Error al cargar chats');
    } finally {
      setLoading(false);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim() || !selectedChat) return;

    try {
      const token = localStorage.getItem('token');
      // Enviamos por axios (como lo ten칤as) pero el socket se encargar치 de recibir la respuesta
      await axios.post(
        `${API_URL}/chat/admin/send-message/${selectedChat._id}`,
        { text: message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage('');
      loadChats(); // Actualizaci칩n r치pida local
    } catch (err) {
      alert('Error al enviar');
    }
  };

  const closeChat = async (chatId) => {
    if (!window.confirm('쮽inalizar chat?')) return;
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/chat/admin/close-chat/${chatId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSelectedChat(null);
      loadChats();
    } catch (err) { alert('Error al cerrar'); }
  };

  return (
    <div className="admin-chat-container" style={{ padding: '20px' }}>
      <h1>游눫 Panel de Soporte (Tiempo Real)</h1>

      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <button 
          onClick={() => { setView('open'); setSelectedChat(null); }}
          style={{
            padding: '10px 20px', borderRadius: '20px', border: 'none',
            background: view === 'open' ? '#667eea' : '#ddd',
            color: 'white', cursor: 'pointer', fontWeight: 'bold'
          }}
        >
          游댠 Chats Activos
        </button>
        <button 
          onClick={() => { setView('closed'); setSelectedChat(null); }}
          style={{
            padding: '10px 20px', borderRadius: '20px', border: 'none',
            background: view === 'closed' ? '#2c3e50' : '#ddd',
            color: 'white', cursor: 'pointer', fontWeight: 'bold'
          }}
        >
          游늬 Historial (Cerrados)
        </button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '350px 1fr', gap: '20px' }}>
        
        {/* COLUMNA IZQUIERDA: LISTA */}
        <div style={{ background: 'white', borderRadius: '8px', border: '1px solid #ddd', height: '70vh', overflowY: 'auto' }}>
          <div style={{ padding: '15px', borderBottom: '1px solid #eee', background: '#fcfcfc' }}>
            <strong>{view === 'open' ? 'En curso' : 'Cerrados'} ({chats.length})</strong>
          </div>
          
          {chats.map(chat => (
            <div 
              key={chat._id}
              onClick={() => setSelectedChat(chat)}
              style={{
                padding: '15px', borderBottom: '1px solid #f5f5f5', cursor: 'pointer',
                background: selectedChat?._id === chat._id ? '#f0f4ff' : 'transparent',
                borderLeft: selectedChat?._id === chat._id ? '4px solid #667eea' : 'none'
              }}
            >
              <div style={{ fontWeight: 'bold', color: '#333' }}>{chat.user?.name}</div>
              <div style={{ fontSize: '12px', color: '#666', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                {chat.messages[chat.messages.length - 1]?.text || 'Sin mensajes'}
              </div>
            </div>
          ))}
        </div>

        {/* COLUMNA DERECHA: MENSAJES */}
        <div style={{ background: 'white', borderRadius: '8px', border: '1px solid #ddd', display: 'flex', flexDirection: 'column', height: '70vh' }}>
          {selectedChat ? (
            <>
              <div style={{ padding: '15px', borderBottom: '1px solid #eee', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <h3 style={{ margin: 0 }}>{selectedChat.user?.name}</h3>
                </div>
                {view === 'open' && (
                  <button onClick={() => closeChat(selectedChat._id)} style={{ background: '#ff4757', color: 'white', border: 'none', padding: '8px 15px', borderRadius: '5px', cursor: 'pointer', fontWeight: '600' }}>
                    Finalizar
                  </button>
                )}
              </div>
              
              <div style={{ flex: 1, padding: '20px', overflowY: 'auto', background: '#f8f9fa' }}>
                {selectedChat.messages.map((m, i) => (
                  <div key={i} style={{ display: 'flex', justifyContent: m.sender === 'admin' ? 'flex-end' : 'flex-start', marginBottom: '15px' }}>
                    <div style={{ 
                      background: m.sender === 'admin' ? '#667eea' : 'white', 
                      color: m.sender === 'admin' ? 'white' : '#333',
                      padding: '10px 15px', borderRadius: '12px', maxWidth: '75%',
                      boxShadow: '0 2px 5px rgba(0,0,0,0.05)',
                      border: m.sender === 'admin' ? 'none' : '1px solid #eee'
                    }}>
                      <p style={{ margin: 0 }}>{m.text}</p>
                      <small style={{ display: 'block', marginTop: '5px', fontSize: '10px', opacity: 0.7 }}>
                         {new Date(m.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </small>
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>

              {view === 'open' && (
                <form onSubmit={sendMessage} style={{ padding: '15px', borderTop: '1px solid #eee', display: 'flex', gap: '10px' }}>
                  <input 
                    type="text" 
                    value={message} 
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Escribe tu respuesta..."
                    style={{ flex: 1, padding: '12px', borderRadius: '8px', border: '1px solid #ddd', outline: 'none' }}
                  />
                  <button type="submit" style={{ background: '#667eea', color: 'white', border: 'none', padding: '10px 25px', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' }}>
                    Enviar
                  </button>
                </form>
              )}
            </>
          ) : (
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%', color: '#aaa' }}>
              <p>Selecciona una conversaci칩n</p>
            </div>
          )}
        </div>
      </div>
