import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, PaymentElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/Checkout.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

const CheckoutForm = ({ shippingAddress, notes, onSuccess }) => {
  const stripe = useStripe();
  const elements = useElements();
  const { clearCart } = useCart();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    // Validar direcci√≥n
    if (!shippingAddress.street || !shippingAddress.city || !shippingAddress.postalCode) {
      setError('Por favor completa la direcci√≥n de env√≠o');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const { error: stripeError, paymentIntent } = await stripe.confirmPayment({
        elements,
        redirect: 'if_required',
        confirmParams: {
          return_url: `${window.location.origin}/perfil`,
        }
      });

      if (stripeError) {
        setError(stripeError.message);
        setLoading(false);
        return;
      }

      if (paymentIntent && paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent.id);
      }
    } catch (err) {
      console.error('Error al procesar pago:', err);
      setError('Error al procesar el pago. Por favor, int√©ntalo de nuevo.');
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="payment-form">
      <div className="payment-section">
        <h3>üí≥ Informaci√≥n de Pago</h3>
        <p className="payment-description">
          Paga de forma segura con tarjeta de cr√©dito o d√©bito
        </p>

        <PaymentElement 
          options={{
            layout: {
              type: 'tabs',
              defaultCollapsed: false,
            }
          }}
        />
      </div>

      {error && (
        <div className="error-message">
          <strong>‚ö†Ô∏è Error:</strong> {error}
        </div>
      )}

      <button 
        type="submit" 
        className="pay-button" 
        disabled={!stripe || loading}
      >
        {loading ? 'Procesando pago...' : 'üí≥ Pagar Ahora'}
      </button>

      <div className="secure-payment">
        <span>üîí</span>
        <p>Pago 100% seguro con Stripe. Aceptamos las principales tarjetas.</p>
      </div>
    </form>
  );
};

const Checkout = () => {
