import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaBox, FaTruck, FaExternalLinkAlt, FaMapMarkerAlt, FaClock, FaCheckCircle, FaShippingFast } from 'react-icons/fa';
import '../../styles/Profile.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const OrdersTab = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedTracking, setExpandedTracking] = useState(null); // Para mostrar/ocultar detalles de tracking

  useEffect(() => {
    const loadOrders = async () => {
      try {
        const token = sessionStorage.getItem('token');
        
        console.log('üîÑ Cargando pedidos del usuario...');
        
        if (!token) {
          console.log('‚ùå No hay token en sessionStorage');
          setError('No est√°s autenticado. Por favor, inicia sesi√≥n.');
          setLoading(false);
          return;
        }

        const response = await axios.get(`${API_URL}/orders/my-orders`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        console.log('‚úÖ Pedidos cargados:', response.data.length || 0);
        setOrders(response.data);
      } catch (error) {
        console.error('‚ùå Error al cargar pedidos:', error.response?.status, error.message);
        
        if (error.response?.status === 401) {
          setError('Tu sesi√≥n ha expirado. Por favor, vuelve a iniciar sesi√≥n.');
        } else {
          setError('Error al cargar tus pedidos. Intenta recargar la p√°gina.');
        }
      } finally {
        setLoading(false);
      }
    };
    loadOrders();
  }, []);

  // Obtener detalles de tracking din√°micamente
  const fetchTrackingDetails = async (trackingNumber) => {
    try {
      const token = sessionStorage.getItem('token');
      const response = await axios.get(`${API_URL}/tracking/${trackingNumber}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (error) {
      console.error('Error obteniendo tracking:', error);
      return null;
    }
  };

  const toggleTrackingDetails = async (orderId, trackingNumber) => {
    if (expandedTracking === orderId) {
      setExpandedTracking(null);
    } else {
      setExpandedTracking(orderId);
      
      // Si no tenemos eventos de tracking, los obtenemos
      const order = orders.find(o => o._id === orderId);
      if (order.tracking && !order.tracking.events) {
        const trackingDetails = await fetchTrackingDetails(trackingNumber);
        if (trackingDetails) {
          // Actualizar el pedido con los eventos
          setOrders(prev => prev.map(o => 
            o._id === orderId 
              ? { ...o, tracking: { ...o.tracking, ...trackingDetails } }
              : o
          ));
        }
      }
    }
  };

  const getStatusIcon = (status) => {
    const icons = {
      'en_preparacion': 'üì¶',
      'enviado': 'üöö',
      'en_transito': '‚úàÔ∏è',
      'en_reparto': 'üö¥',
      'entregado': '‚úÖ',
      'pendiente': '‚è≥'
    };
    return icons[status] || 'üìã';
  };

  const getStatusColor = (status) => {
    const colors = {
      'en_preparacion': '#f39c12',
      'enviado': '#3498db',
      'en_transito': '#9b59b6',
      'en_reparto': '#e67e22',
      'entregado': '#27ae60',
      'pendiente': '#95a5a6'
    };
    return colors[status] || '#95a5a6';
  };

  const formatPrice = (price) => new Intl.NumberFormat('es-ES', { style: 'currency', currency: 'EUR' }).format(price);

  if (loading) return <div className="tab-loading">Cargando tus pedidos...</div>;

  if (error) return (
    <div className="empty-state">
      <FaBox size={48} />
      <h3>Error</h3>
      <p>{error}</p>
    </div>
  );

  if (orders.length === 0) return (
    <div className="empty-state">
      <FaBox size={48} />
      <h3>A√∫n no tienes pedidos</h3>
      <p>Cuando realices tu primera compra, aparecer√° aqu√≠.</p>
    </div>
  );

  return (
    <div className="orders-tab">
      <h2>Historial de Pedidos</h2>
      <div className="orders-list">
        {orders.map(order => (
          <div key={order._id} className="order-card">
            <div className="order-header">
              <div className="order-meta">
                <span className="order-id">#{order._id.slice(-8).toUpperCase()}</span>
                <span className="order-date">
                  {new Date(order.createdAt).toLocaleDateString('es-ES', {
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric'
                  })}
                </span>
              </div>
              <span className={`status-badge ${order.orderStatus}`}>
                {order.orderStatus.charAt(0).toUpperCase() + order.orderStatus.slice(1)}
              </span>
            </div>

            {/* Informaci√≥n de Seguimiento Mejorada */}
           {order.tracking && (
  <div className="tracking-section-enhanced">
    <div className="tracking-card">
      <div className="status-header">
        <FaTruck className="truck-anim" />
        <div>
          <h4>Estado del Env√≠o</h4>
          <p>Localizador: <strong>{order.tracking.trackingNumber}</strong></p>
        </div>
      </div>
      
      {/* Barra de progreso visual */}
      <div className="progress-track">
        <div className={`step ${order.tracking.currentStatus ? 'active' : ''}`}>üì¶ Preparado</div>
        <div className={`step ${['enviado', 'en_transito', 'entregado'].includes(order.tracking.currentStatus) ? 'active' : ''}`}>üöö En camino</div>
        <div className={`step ${order.tracking.currentStatus === 'entregado' ? 'active' : ''}`}>üè† Entregado</div>
      </div>

      <button 
        className="btn-details"
        onClick={() => toggleTrackingDetails(order._id, order.tracking.trackingNumber)}
      >
        {expandedTracking === order._id ? 'Cerrar historial' : 'Rastrear paquete'}
      </button>
    </div>
    
    {expandedTracking === order._id && (
      <div className="timeline-dropdown">
        {/* Aqu√≠ renderizas tus eventos como ya ten√≠as en el c√≥digo anterior */}
      </div>
    )}
  </div>
)}

            {/* Productos del Pedido */}
            <div className="order-items">
              {order.items && order.items.length > 0 ? (
                order.items.map((item, i) => (
                  <div key={i} className="order-item">
                    <img 
                      src={item.product?.images?.[0] || 'https://via.placeholder.com/50'} 
                      alt={item.product?.name || 'Producto'} 
                    />
                    <div className="item-details">
                      <p>{item.product?.name || 'Producto'}</p>
                      <small>Cantidad: {item.quantity}</small>
                    </div>
                    <span className="item-price">
                      {formatPrice(item.price * item.quantity)}
                    </span>
                  </div>
                ))
              ) : (
                <p style={{padding: '10px', color: '#999', fontSize: '0.9rem'}}>
                  Sin productos
                </p>
              )}
            </div>

            <div className="order-footer">
              <div className="order-payment-info">
                <span className={`payment-badge ${order.paymentStatus}`}>
                  {order.paymentStatus === 'pagado' ? '‚úì Pagado' : 'Pendiente de pago'}
                </span>
              </div>
              <span className="order-total">
                Total: <strong>{formatPrice(order.totalAmount)}</strong>
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrdersTab;
